sys_script.script:
/*
|--------------------------------------------------------------------------
|	OnInsert
|--------------------------------------------------------------------------
|
|	Autores:[
|		Yuhri Bernardes - 05/02/2019
|	]
|
|	Ações: [
|		1 - Mapeamento do registro de voo
|		2 - Atualização do registro atual
|		3 - Atualização das notas gerais
|	]
|
*/

(function executeRule(current, previous /*null when async*/ ) {

	var util = new x_alabs_pex.PexScoreUtils();

	//OBS é necessário fazer a ação 1 para que a ação 3 funcione

	/*
	|--------------------------------------------------------------------------
	|	Ação 1
	|--------------------------------------------------------------------------
	*/

	var query = {
		numero_do_voo: current.flight_number.toString().replace(/[.|,]/g, "").trim(),
		"origem.u_iata": current.origin.toString().trim(),
		"destino.u_iata": current.destination.toString().trim(),
		data_partida_utc: current.flight_local_date.toString().trim()
	};


	util.Flight.get(query);

	if (util.Flight.count)
		current.flight = util.Flight.data[0].getUniqueValue();

	/*
	|--------------------------------------------------------------------------
	|	Ação 2
	|--------------------------------------------------------------------------
	*/
	current.update();

	/*
	|--------------------------------------------------------------------------
	|	Ação 3
	|--------------------------------------------------------------------------
	*/

	util.NPS.getByID(current.getUniqueValue());

	util.updateNPS();




})(current, previous);
